var LINK_STYLE_REGEX = /\[(.+?)\]\(#([\w\-]+?):(\d+?\.?\d*?)\)/g;
var CODE_BLOCK_REGEX = /`{3}[\s\S]*?`{3}/gm;
var INLINE_CODE_REGEX = /`[\s\S]*?`/g;
var CODE_STYLE_PATTERN = "([A-Z]{2,}):?(\\d+?\\.?\\d*?)?\\s+(.*)$";
var HASH_STYLE_REGEX = /#([\w\-]+?):(\d+?\.?\d*?)\s+(.*)/g;

