'use strict';

var _          = require('lodash'),
    Project    = require('./project'),
    Repository = require('./repository');

var MISSING_PROJECT = 'project is a required option and must be a Repository or Project object',
    MISSING_QUERY   = 'query is a required option';
/**
 * opts : {
 * project: a Project or Repository object
 * query: a regular expression query
 * offset: The offset
 * limit: max results to return
 * }
 * @method Search
 * @param {} opts
 * @return 
 */
var Search = function(opts) {
  opts = _.extend({
    offset:0,
    limit: 200
  }, opts || {});

  if ( !opts.project || 
       ( !(opts.project instanceof Project) &&
         !(opts.project instanceof Repository) ) ) throw new Error(MISSING_PROJECT);

  if (!opts.query) throw new Error(MISSING_QUERY);

  opts.re = new RegExp('(' + opts.query + ')', 'i');
  this.opts = opts;
  this.result = [];
  this.hits = 0;
  this.total = 0;
  this.filesSearched = 0;
  this.filesNotSearched = 0;
};

/**
 * Description
 * @method execute
 * @return MemberExpression
 */
Search.prototype.execute = function() {
  this.find(this.opts.project);
  return this.result;
};

/**
 * Description
 * @method find
 * @return 
 */
Search.prototype.find = function() {
  var self = this, project = this.opts.project;

  project.getFilesInPath().forEach(function(file) {

    if (self.total >= self.opts.limit) {
      self.filesNotSearched++;
      return;
    }

    self.filesSearched++;

    var lines = [];
    var str = project.readFileContentSync(file).getContent();
    file = file.getSource();
    str.split('\n').forEach(function(line, i){
      var pos = line.search(self.opts.re);
      if (pos > -1) {
        if (line.length > 120) {
          if (pos > 120) {
            var truncated = line.substring(pos-60, pos+60);
            if (truncated.length < 120) {
              truncated = line.substring(line.length-120);
              line = "... " + truncated;
            } else {
              line = "... " + truncated + " ...";
            }
          } else {
            line = line.substring(0,120) + "...";
          }
        }
        lines.push([i+1, line]);
      }
    });

    if (lines.length > 0) {
      var fileResult = {file:file, lines:[]};
      lines.forEach(function(line){
        self.hits++;
        if ((self.hits > self.opts.offset) && (self.total < self.opts.limit)) {
          fileResult.lines.push({line:line[0], text:line[1]});
          self.total++;
        }
      });
      if (fileResult.lines.length > 0) self.result.push(fileResult);
    }

  });
};

/**
 * Description
 * @method toJSON
 * @return ObjectExpression
 */
Search.prototype.toJSON = function() {
  var self = this;
  return {
    opts: {
      project: self.opts.project.name,
      query: self.opts.query,
      offset: self.opts.offset,
      limit: self.opts.limit
    },
    hits: self.hits,
    total: self.total,
    filesSearched: self.filesSearched,
    filesNotSearched: self.filesNotSearched,
    result: self.result
  };
};

module.exports = Search;

var path  = require('path'),
    _     = require('lodash'),
    fs    = require('fs'),
    log   = require('debug')('tree'),
    tools = require('imdone-core').Tools;

function getFiles(_path) {
  if (!_path) _path = tools.userHome();

  log("Path:", _path);
  var files =  fs.readdirSync(_path);
  log("raw files:", files);
  files = _.map(files, function(file) { return path.join(_path, file); } ).sort();

  var up = path.resolve(_path + path.sep + '..' + path.sep);
  var name = path.basename(_path);
  var hidden = /^\./.test(name);
  var out = { path:_path, name: name, hidden: hidden, up: up};

  _.each(files, function(file) {
    log(file);
    var name = path.basename(file);
    var hidden = /^\./.test(name);
    if (fs.statSync(file).isDirectory()) {
      if(!out.dirs) out.dirs = [];
      out.dirs.push({name:name, hidden:hidden, path:file});
    } else {
      if(!out.files) out.files = [];
      out.files.push({name:name, hidden:hidden, path:file});
    }
  });
  return out;
}

module.exports = {
  getFiles: getFiles
};

'use strict';

var _                = require('lodash'),
    fs               = require('fs'),
    events           = require('events'),
    util             = require('util'),
    File             = require('./file'),
    tools            = require('./tools'),
    Repository       = require('./repository'),
    log              = require('debug')('imdone-core:Watched-Repository');

// Emits task.found, list.found, file.update and file.delete, file.processed
/**
 * A Repository is a file system directory in which to look for tasks in files.  The Repository manages all the files
 * and lists in it's path.
 *
 * @constructor
 * @param {} path
 * @param {} config
 * @return 
 */
function WatchedRepository(path, config) {
  Repository.call(this, path, config);
}

util.inherits(WatchedRepository, events.EventEmitter);
util.inherits(WatchedRepository, Repository);

/**
 * Description
 * @method init
 * @param {} cb
 * @return 
 */
WatchedRepository.prototype.init = function(cb) {
  var self = this;
  cb = tools.cb(cb);

  WatchedRepository.super_.prototype.init.call(this, function(err, files) {
    if (self.config.watcher) { self.initWatcher(); }
    self.emit('initialized', {ok:true});
    log('initialized');
    cb(err,files);
  });  
};

/**
 * Description
 * @method destroy
 * @return 
 */
WatchedRepository.prototype.destroy = function() {
  if (this.watcher) this.watcher.close();
  WatchedRepository.super_.prototype.destroy.apply(this);
};

/**
 * Description
 * @method initWatcher
 * @return 
 */
WatchedRepository.prototype.initWatcher = function() {
  var self = this;
  log("Creating a new watcher");
  this.watcher = require('chokidar').watch(self.path, {
    /**
     * Description
     * @method ignored
     * @param {} path
     * @return exclude
     */
    ignored: function(path) {
      var relPath = self.getRelativePath(path);
      var exclude = self.shouldExclude(relPath);
      return exclude;
    }, 
    persistent: true
  });

  this.watcher
  .on('add', function(path) {
    log("Watcher received add event for file: " + path);
    var relPath = self.getRelativePath(path);
    var file = self.getFile(relPath);
    if (file === undefined) {
      file = new File(self.getId(), relPath);
    } else if (fs.statSync(path).mtime <= file.getModifiedTime()) return;

    if (self.fileOK(file)) {
      log("Reading file: " + path);
      self.readFile(file, function (err, file) {
        self.emit("file.update", file);
      });
    }
  })
  .on('addDir', function(path) {log('Directory', path, 'has been added');})
  .on('change', function(path) {
    log("Watcher received change event for file: " + path);
    var file = self.getFile(self.getRelativePath(path));
    if (self.fileOK(file)) {
      log("Reading file: " + path);
      self.readFile(file, function (err, file) {
        self.emit("file.update", file);
      });
    }
  })
  .on('unlink', function(path) {
    log("Watcher received unlink event for file: " + path);
    var file = new File(self.getId(), self.getRelativePath(path));
    log("Removing file: " + path);
    self.removeFile(file);
  })
  .on('unlinkDir', function(path) {log('Directory', path, 'has been removed');})
  .on('error', function(error) {console.error('Error while watching files:', error);});

};

module.exports = WatchedRepository;

'use strict';

var _      = require('lodash'),
    events = require('events'),
    fs     = require('fs'),
    async  = require('async'),
    util   = require('util'),
    tools  = require('./tools'),
    Config = require('./config'),
    Project = require('./project');

var DEFAULT_CONFIG = { lists: [] };

/**
 * Description
 * @method Project
 * @param {} owner
 * @param {} name
 * @param {} repos
 * @return 
 */
function FSProject(owner, name, repos) {
  Project.call(this, owner, name, repos);

  if (!fs.existsSync(this.configBase) || !fs.statSync(this.configBase).isDirectory())
    throw new Error("IMDONE_CONFIG_DIR must be an existing directory on the file system");
}

util.inherits(FSProject, Project);

FSProject.prototype.loadConfig = function() {
  var file = this.getConfigFile();
  var configLike = {};
  if (fs.existsSync(file)) {
    var configLikeContent = fs.readFileSync(file);
    try {
      configLike = JSON.parse(configLikeContent.toString());
    } catch (e) {}
  }

  _.defaults(configLike, _.cloneDeep(DEFAULT_CONFIG));
  this.config = new Config(configLike);

  return this.config;
};

FSProject.prototype.saveConfig = function(cb) {
  var self = this;
  if (cb === undefined) cb = _.noop;
  // Only store config if there is more than one Repo
  if (this.getRepos().length === 1) return cb();
  var dir = this.getConfigDir();
  var file = this.getConfigFile();

  fs.exists(dir, function(exists) {
    try {
      if (!exists) {
        tools.mkdirSyncRecursive(fs, dir);
      }
      fs.writeFile(file, JSON.stringify(self.getConfig(), null, 2), cb);
    } catch(e) {
      console.log("Error saving config " + file);
      cb();
    }
  });
};

module.exports = FSProject;

